class i{constructor(t=0,r=0,e=0){if(t<=24&&t>0)this.hour=t;else throw Error("Timmar m\xE5ste vara mellan 0 och 24");if(r<=60&&r>0)this.minute=r;else throw Error("Minuter m\xE5ste vara mellan 0 och 60");if(e<=60&&e>0)this.second=e;else throw Error("Sekunder m\xE5ste vara mellan 0 och 60")}setTime(t,r,e){if(t<=24&&t>0)this.hour=t;else throw Error("Timmar m\xE5ste vara mellan 0 och 24");if(r<=60&&r>0)this.minute=r;else throw Error("Minuter m\xE5ste vara mellan 0 och 60");if(e<=60&&e>0)this.second=e;else throw Error("Sekunder m\xE5ste vara mellan 0 och 60")}tick(){new i(1,1,1);let t=this.second;t<=60&&t>0&&this.second++,this.second==60&&(this.second=0,this.minute+=1),this.minute==60&&(this.minute=0,this.hour+=1),this.hour==24&&(this.hour=0)}get timeHour(){return this.hour.toString().padStart(2,"0")}get timeMinute(){return this.minute.toString().padStart(2,"0")}get timeSecond(){return this.second.toString().padStart(2,"0")}}export{i as C};
